import { receiveMessage, sendMessageToUi } from "./helpers/messenger";
import { Database } from "./helpers/storage";


const main = async () => {
  const db = await Database.init()
  const urlList = await UrlList.init(db)
  const beeper = new Beeper(db)

  receiveMessage("activation-toggled", async (message) => {
    if (message.type === "activation-toggled") {
      const isActive = await db.get("isActive")
      const description = "until reactivated"

      await db.set("isActive", !isActive)
      await db.set("statusDescription", description)
      await sendMessageToUi("set-activation-toggle", { isActive: !isActive, description })
    }
  })


  if (true || urlList.includesUrl(window.location.host)) {
    window.addEventListener('focus', function(this: Beeper) {
      beeper.unMute()
    })

    window.addEventListener('blur', function(this: Beeper) {
      beeper.mute()
    })

    beeper.startLoop()
  }
}


class Beeper {
  isMuted: boolean = false
  private db: Database

  constructor(db: Database) {
    this.db = db
  }

  startLoop = async () => {
    this.beepIfActive()
    while (true) {
      await this.waitRandomInterval()
      await this.beepIfActive()
    }
  }

  beepIfActive = async () => {
    if (this.isMuted) {
      return
    }

    if (await this.db.get("isActive")) {
      Beeper.sound.play()
    }
  }

  mute = () => {
    this.isMuted = true
  }

  unMute = () => {
    this.isMuted = false
  }

  waitRandomInterval = async () => {
    await new Promise(r => setTimeout(r, this.MIN_INTERVAL + Math.random() * (this.MAX_INTERVAL - this.MIN_INTERVAL)));
  }

  private MIN_INTERVAL = 1000
  private MAX_INTERVAL = 60000
  static sound = new Audio("data:audio/wav;base64,")
}


class UrlList {
  urlList: string[]

  constructor(urlList: string[]) {
    this.urlList = urlList
  }

  static init = async (db: Database) => {
    return new UrlList(await db.get("urlList"))
  }

  addUrl = async (url: string) => {
    this.urlList.push(url)
  }

  removeUrl = async (url: string) => {
    this.urlList = this.urlList.filter((value) => value !== url)
  }

  includesUrl = (url: string) => {
    return this.urlList.includes(url)
  }
}


